color.template:
{{ $status := .status.phase }}
{{ $color := "gray" }}
{{ if list "Provisioning" "DataRestoring" | has $status }}
  {{ $color = "info" }}
{{ else if list "Ready" | has $status }}
  {{ $color = "success" }}
{{ else if list "Critical" | has $status }}
  {{ $color = "warning" }}
{{ else if list "NotReady" | has $status }}
  {{ $color = "danger" }}
{{ else if list "Halted" | has $status }}
  {{ $color = "gray" }}
{{ end }}
{{- printf "%s" $color -}}

query.raw:
request:
  embeddedLink: true
  dashboards:
  - title: "KubeDB / MongoDB / Summary"
    vars:
    - name: namespace
      value: {{ .namespace }}
    - name: app
      value: {{ .name }}
    panels:
      - { "title": "CPU Usage", "width": 12 }
      - { "title": "Memory Usage", "width": 12 }
      - { "title": "Disk Usage", "width": 6 }
      - { "title": "Disk R/W Info", "width": 6 }
      - { "title": "IOPS (Reads+Writes)", "width": 6 }
      - { "title": "ThroughPut (Read+Write)", "width": 6 }
      - { "title": "Receive Bandwidth", "width": 6 }
      - { "title": "Transmit Bandwidth", "width": 6 }

query Find($src: String!, $targetGroup: String!, $targetKind: String!) {
  find(oid: $src) {
    offshoot(group: "", kind: "Pod") {
      refs: view(group: $targetGroup, kind: $targetKind) {
        namespace
        name
      }
    }
  }
}

color.template:
{{ $status := .status.phase }}
{{ $color := "gray" }}
{{ if list "Running" "Succeeded" | has $status }}
  {{ $color = "success" }}
{{ else if list "Pending" | has $status }}
  {{ $color = "info" }}
{{ else if list "Failed" | has $status }}
  {{ $color = "danger" }}
{{ else if list "Unknown" | has $status }}
  {{ $color = "warning" }}
{{ end }}
{{- printf "%s is-light" $color -}}

{{ $operation := .operation | lower }}
{{ $color := "gray" }}
{{ if list "command" | has $operation }}
  {{ $color = "primary" }}
{{ else if list "insert" "group" | has $operation }}
  {{ $color = "success" }}
{{ else if list "count" "distinct" "mapreduce" | has $operation }}
  {{ $color = "info" }}
{{ else if list "update" "query" | has $operation }}
  {{ $color = "warning" }}
{{ else if list "remove" | has $operation }}
  {{ $color = "danger" }}
{{ else if list "geonear" "getmore" | has $operation }}
  {{ $color = "dark" }}
{{ end }}
{{- printf "%s" $color -}}

